        {
            "objectiveRef": "Deliver",
            "contact": {
                "name": "{{$randomFirstName}}",
                "phone": {
                    "countryCode": "{{$randomCountry}}",
                    "number": "{{$randomPhoneNumber}}",
                    "isMobile": true
                }
            },
            "location": "location:abce4567-e89b-12d3-a456-426614174000",
            "input": {
                "scan": {
                    "scanType": "QRCODE",
                    "scanMode": "MULTI"
                },
                "pick": {
                    "items": [
                        {
                            "sku": "21234"
                        },
                        {
                            "sku": "22345"
                        }
                    ]
                },
                "cancel": {
                    "cancellationReasons": [
                        "Not available",
                        "Out of town"
                    ]
                }
            }
        }
    ]
}

{
	"name": {{$randomNoun}},
	"entityCode": "0011",
	"description": {{$randomLoremSentence}},
	"tag": [
		{
			"name": "usage",
			"value": "Utility"
		}
	],
	"inputs": {
		"type": "object",
		"properties": {
			"resolution": {
				"title": "Resolution",
				"description": "Resolution of image to be captured",
				"type": "object",
				"properties": {
					"length": {
						"title": "Length",
						"description": "Length (in px)",
						"type": "number",
						"exclusiveMinimum": 0
					},
					"breadth": {
						"title": "Breadth",
						"description": "Breadth (in px)",
						"type": "number",
						"exclusiveMinimum": 0
					}
				},
				"required": ["length", "breadth"]
			},
			"fileSizeLimit": {
				"title": "Max file size",
				"type": "number",
				"description": "Maximum Size limit (in kb) of image to be captured",
				"exclusiveMinimum": 0
			},
			"captureMode": {
				"title": "Capture Mode",
				"description": "Mode of capturing image",
				"type": "string",
				"enum": ["SINGLE", "MULTI"]
			},
			"captureCountLimit": {
				"title": "Max image count",
				"type": "number",
				"description": "Max limit of images to be captured in multi-capture mode",
				"minimum": 1
			},
			"title": {
				"title": "Title",
				"type": "string",
				"description": "Title of instruction"
			},
			"message": {
				"title": "Message",
				"type": "string",
				"description": "Message of instruction"
			},
			"canGoBack": {
				"title": "Can go back",
				"type": "boolean",
				"description": "Can go back"
			},
			"applicableReasons": {
				"title": "Applicable reasons",
				"description": "applicable reason codes",
				"type": "object"
			}
		},
		"required": [
			"resolution",
			"fileSizeLimit",
			"captureMode",
			"title",
			"applicableReasons"
		]
	},
	"output": {
		"type": "object",
		"properties": {
			"success": {
				"description": "Success flag for the operation performed by user",
				"type": "boolean"
			},
			"captures": {
				"type": "array",
				"description": "Array of captured images",
				"items": {
					"type": "object",
					"description": "Object to describe details of single image",
					"properties": {
						"url": {
							"type": "string",
							"description": "Url of image"
						},
						"timestamp": {
							"type": "number",
							"description": "Epoch timestamp for the image capture"
						},
						"file": {
							"type": "object",
							"description": "details of file",
							"properties": {
								"folder": {
									"type": "string",
									"description": "folder name"
								},
								"name": {
									"type": "string",
									"description": "name of the file"
								}
							}
						}
					},
					"required": ["url", "timestamp"]
				}
			},
			"eventCode": {
				"type": "string",
				"description": "Event code"
			},
			"reasonCode": {
				"type": "string",
				"description": "Reason code"
			},
			"meta": {
				"type": "object",
				"description": "Meta data related to execution of task",
				"properties": {
					"startTime": {
						"type": "number",
						"description": "Epoch timestamp for the start of task"
					},
					"endTime": {
						"type": "number",
						"description": "Epoch timestamp for the end of task"
					},
					"location": {
						"type": "object",
						"description": "Location at start of task",
						"properties": {
							"latitude": {
								"type": "string",
								"description": "Latitude"
							},
							"longitude": {
								"type": "string",
								"description": "Longitude"
							}
						}
					},
					"userDetails": {
						"type": "string",
						"description": "Unique identifier of user performing the action"
					}
				}
			}
		},
		"required": ["success"]
	},
	"canMerge": false
}

{
    "flows": [
        {
            "description": "Initiate payment",
            "id": "etask:4eb8d36b-6762-5c53-aca6-95b1f4e3aa3f",
            "name": "InitPayment",
            "next": [
                {
                    "task": "ProcessPayment",
                    "condition": "initPaymentSuccess"
                },
                {
                    "task": "macroFailed",
                    "condition": "initPaymentFailure"
                }
            ]
        },
        {
            "description": "Process payment",
            "id": "etask:9a559375-02a5-50fe-9ce4-0a36df6f324e",
            "name": "ProcessPayment",
            "next": [
                {
                    "task": "CompletePayment",
                    "condition": "processSuccess"
                },
                {
                    "task": "macroFailed",
                    "condition": "processFailure"
                }
            ]
        },
        {
            "description": "Complete payment",
            "id": "etask:0e6c7bd7-c9ab-5b82-85b2-a054249bc1f1",
            "name": "CompletePayment",
            "next": [
                {
                    "task": "macroSucceeded",
                    "condition": "completeSuccess"
                },
                {
                    "task": "macroFailed",
                    "condition": "completeFailure"
                }
            ]
        }
    ],
    "name": "Collect Cash Macro",
    "conditions": {
        "initPaymentSuccess": {
            "and": [
                {
                    "==": [
                        "$.output.entityCode",
                        "0017"
                    ]
                },
                {
                    "==": [
                        "$.output.eventCode",
                        "E-010"
                    ]
                }
            ]
        },
        "initPaymentFailure": {
            "and": [
                {
                    "==": [
                        "$.output.entityCode",
                        "0017"
                    ]
                },
                {
                    "==": [
                        "$.output.eventCode",
                        "E-011"
                    ]
                },
                {
                    "==": [
                        "$.output.reasonCode",
                        "R-0020"
                    ]
                }
            ]
        },
        "processSuccess": {
            "and": [
                {
                    "==": [
                        "$.output.entityCode",
                        "0018"
                    ]
                },
                {
                    "==": [
                        "$.output.eventCode",
                        "E-010"
                    ]
                }
            ]
        },
        "processFailure": {
            "and": [
                {
                    "==": [
                        "$.output.entityCode",
                        "0018"
                    ]
                },
                {
                    "==": [
                        "$.output.eventCode",
                        "E-011"
                    ]
                },
                {
                    "==": [
                        "$.output.reasonCode",
                        "R-0020"
                    ]
                }
            ]
        },
        "completeSuccess": {
            "and": [
                {
                    "==": [
                        "$.output.entityCode",
                        "0019"
                    ]
                },
                {
                    "==": [
                        "$.output.eventCode",
                        "E-010"
                    ]
                }
            ]
        },
        "completeFailure": {
            "and": [
                {
                    "==": [
                        "$.output.entityCode",
                        "0019"
                    ]
                },
                {
                    "==": [
                        "$.output.eventCode",
                        "E-011"
                    ]
                },
                {
                    "==": [
                        "$.output.reasonCode",
                        "R-0020"
                    ]
                }
            ]
        }
    },
    "canMerge": false,
    "systemGenerated": true,
    "inputs": {
        "InitPayment": {
            "$val": {
                "amount": {
                    "$expr": "$.inputs.amount"
                },
                "allowedModes": {
                    "$val": [
                        "CASH"
                    ]
                },
                "title": {
                    "$val": "Collect Payment"
                },
                "applicableReasons": {
                    "$val": {
                        "E-010": {
                            "$val": []
                        },
                        "E-011": {
                            "$val": [
                                "R-0020"
                            ]
                        }
                    }
                }
            }
        },
        "ProcessPayment": {
            "$val": {
                "amount": {
                    "$expr": "$.inputs.amount"
                },
                "paymentMode": {
                    "$val": "CASH"
                },
                "title": {
                    "$val": "Processing Payment"
                },
                "transactionId": {
                    "$expr": "$.InitPayment.output.transactionId"
                },
                "applicableReasons": {
                    "$val": {
                        "E-010": {
                            "$val": []
                        },
                        "E-011": {
                            "$val": ["R-0020"]
                        }
                    }
                }
            }
        },
        "CompletePayment": {
            "$val": {
                "expectedAmount": {
                    "$expr": "$.inputs.amount"
                },
                "collectedAmount": {
                    "$expr": "$.ProcessPayment.output.collectedAmount"
                },
                "transactionId": {
                    "$expr": "$.ProcessPayment.output.transactionId"
                },
                "tenantTransactionId": {
                    "$expr": "$.ProcessPayment.output.tenantTransactionId"
                },
                "paymentMode": {
                    "$expr": "$.ProcessPayment.output.paymentMode"
                },
                "title": {
                    "$val": "Payment Success"
                },
                "applicableReasons": {
                    "$val": {
                        "E-010": {
                            "$val": []
                        },
                        "E-011": {
                            "$val": ["R-0020"]
                        }
                    }
                }
            }
        }
    }
}

{
  "name": "SL_TEST_COD_PICKUP_WF_1642738631197",
  "description": "COD Drop Workflow",
  "tag": [
    {
      "name": "objective",
      "value": "Drop"
    },
    {
      "name": "mode",
      "value": "cod"
    }
  ],
  "flows": [
    {
      "name": "Start",
      "id": "etask:952f3754-12e8-5ff2-93a8-aca383d18e56",
      "description": "Start ET",
      "next": [
        {
          "condition": "webWfStarted",
          "task": "WebCollectPayment"
        },
        {
          "condition": "wfStarted",
          "task": "CollectPayment"
        },
        {
          "condition": "wfFailedVerifyLocation",
          "task": "VerifyLocation"
        },
        {
          "condition": "wfFailedForm",
          "task": "Form"
        },
        {
          "condition": "wfFailedDisplay",
          "task": "Display"
        }
      ]
    },
    {
